package body fonttable is
    function ascii_to_pixel(
        signal ascii : std_logic_vector(7 downto 0);
        signal pos_x : integer range 0 to CHAR_WIDTH - 1;
        signal pos_y : integer range 0 to CHAR_HEIGHT - 1
    ) return std_logic is
        variable char_pos  : integer;
        variable pixel_pos : integer;
    begin
        if ascii /= "UUUUUUUU" and unsigned(ascii) >= 32 then
            char_pos  := to_integer(unsigned(ascii)) - 32;
            pixel_pos := pos_y * CHAR_WIDTH + pos_x;

            return FONT_TABLE(char_pos)(pixel_pos);
        else
            -- invalid char
            return '1';
        end if;
    end function;
end package body;